import React from "react";
import "./style.css";
import githubIcon from '../../utils/images/icons/github.png';


const imgStyle = {
    width: '50%',
    position: 'relative',
}

const descStyle = {
    position: 'relative',
    float: 'left'
}

const project = {
    paddingBottom: '20px'
  }

const projectTitle = {
    position: 'relative',
    paddingRight: '20px',
    fontSize: '40px',
    fontWeight: '400'
  }

const icon = {
    position: 'relative',
    float: 'left',
    width: '50px',
    padding: '5px',
    opacity: '0.7',
    zIndex: '10'
}

const imageContainer = {
    float: 'left',
    width: '60%',
    paddingRight: '20px',
    marginBottom: '40px',
    zIndex: '20'
}

const screenshot = {}

function Project(props) {
    const prop = props.children
  return (
    <div>
        <div style={project}>
            <div style={projectTitle}>{prop.title}</div>
            <a href="https://tzvik15.github.io/Bonez-Beatz/" rel="noopener noreferrer" target="_blank">
                <div style={imageContainer}><img alt="" stlye={screenshot} src={prop.image}/></div>
            </a>
            <a href="https://github.com/tzvik15/Bonez-Beatz" rel="noopener noreferrer" target="_blank">
                <div class="gitLink">
                    <img src={githubIcon} alt="" style={icon}/>
                </div>
            </a>
            <div class="project-description">
                    {prop.desc}
            </div>
        </div>
    </div>
  );
}

export default Project;
