import React from "react";
import "./style.css";
// import image from "../../utils/photos/brand.jpg";

const imgStyle = {
    width: '50%',
    position: 'relative',
}

const descStyle = {
    position: 'relative',
    float: 'left'
}

function Project(props) {
    const prop = props.children
  return (
    <div>
        <h2>{prop.title}</h2>
        <img style={imgStyle} src={prop.image}></img>
        <p style={descStyle}>{prop.desc}</p>
    </div>
  );
}

export default Project;
